/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  /* Custom theme options */
  --color-accent: white;
  --color-accent2: #260055;
  --color-shade: #999999;

  --inner-max-width: 800px;

  /* Base values */
  --box-shadow: 0px 2px 8px rgba(0, 0, 0, 0.1);
  --border-radius: 2px;
  --border-color: #e0e1e1;
  --body-font: "Merriweather", serif;
  --heading-font: "Inter", sans-serif;

  --header-height: 92.8px;
}

/* Root element (use instead of `body`) */
#page {
  font-family: var(--heading-font);
  color: white;
  line-height: 1.5;
  font-size: 1.125rem;
  background: black;
}

/* Elements */
.section-container {
  max-width: 1150px;
  margin: 0 auto;
  padding: 5rem 2rem;
}

a.link {
  line-height: 1.3;
  border-bottom: 2px solid var(--color-accent);
  transform: translateY(-2px); /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-size: clamp(2rem, 10vw, 3rem);
  line-height: 1.1;
  font-weight: 600;
  letter-spacing: -0.15rem;
}

.button {
  color: white;
  background: var(--color-accent, rebeccapurple);
  border-radius: 0;
  padding: 1px 24px;
  transition: var(--transition, 0.1s box-shadow);
  border: 0; /* reset */
  border-radius: 0.25rem;
  font-size: 1rem;

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

.hero {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 70vh;
  position: relative;
  overflow-x: clip;
}

.zoom-container:hover {}

.image {
  width: 350px;
  transition: all 0.6s ease;
  cursor: pointer;
  position: absolute;
}

.image.career {
  padding-left: 200px;
  padding-bottom: 50px;
}

.image.hobby {
  padding-right: 200px;
}

.career {
  position: absolute;
  transform: translate(-85%, -45%);
}

.hobby {
  position: absolute;
  transform: translate(3%, -50%);
}

.label {
  position: absolute;
  font-size: 5em;
  font-weight: 800;
  opacity: 0.6;
  transition: all 0.6s ease;
  pointer-events: none;

  background: linear-gradient(to bottom, #FFFFFF 30%, #8A8A8A 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.label.career {
  transform: translate(-100%, 90%);
}

.label.hobby {
  transform: translate(20%, 50%);
}

.image.career:hover {
  transform: translate(-87%, -45%) scale(1.01);
}

.image.hobby:hover {
  transform: translate(5%, -50%) scale(1.02);
}

.career:hover ~ .label.career,
.hobby:hover ~ .label.hobby {
  opacity: 1;
  margin-top: -1%;

  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.gradient-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 80%;
  background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 1) 80%);
  pointer-events: none;
}

.gradient-background {
  z-index: -1;
  position: absolute;
  width: 1500px;
  height: 1045px;
  transform: translate(-50%, -64%);

  background-size: 100% 100%;
  background-position: 0px 0px, 0px 0px, 0px 0px;
  opacity: 0;

  transition: all 0.6s ease;
}

.gradient-background-career {
  background-image: radial-gradient(73% 293% at 59% 34%, #132BC40F 0%, #FF070700 36%),radial-gradient(73% 120% at 42% 66%, #30113E52 0%, #FF070700 36%),radial-gradient(75% 229% at 50% 50%, #02080CFF 1%, #000000FF 16%);
}

.gradient-background-hobby {
  background-image: radial-gradient(73% 293% at 65% 34%, #3E7C120A 0%, #FF070700 36%),radial-gradient(73% 97% at 57% 66%, #0B2A0D52 0%, #FF070700 36%),radial-gradient(169% 229% at 54% 50%, #0C0C02FF 1%, #000000FF 16%);
}

.career:hover ~ .gradient-background-career,
.hobby:hover ~ .gradient-background-hobby {
  opacity: 1;
}

section {
  background: var(--color-background, black);
  color: white;
}

.section-container {
} /* Defined in Site CSS */

.teasers {
  display: grid;
  gap: 6rem;
}

.teaser {
  display: grid;
