  img {
    width: 100%;
    border-radius: var(--border-radius);
  }

  .content-section {
    /* Defined in Site CSS */
    display: grid;
    place-items: flex-start;
    padding: 0;

    .heading {
      /* Defined in Site CSS */
      margin-bottom: 1.25rem;
      margin-left: 0;
    }

    .body {
      margin-bottom: 2rem;
    }

    a.button {
      background-color: transparent;
      border: 1.5px solid white;

      &:hover {
