      }
    } /* Defined in Site CSS */
  }

  @media (min-width: 800px) {
    grid-template-columns: 1fr 1fr;
    place-items: center;
    gap: 4rem;

    /* switch direction for even items */
    &:nth-child(odd) img {
      grid-row: 1;
