  display: grid;
  gap: 3rem;
  place-items: center;

  @media (min-width: 700px) {
    grid-template-columns: 1.5fr 1fr;
    gap: 7rem;
  }
} 

.content {
  :global(img) {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  :global(p) {
    padding: 0.25rem 0;
    line-height: 1.5;
    font-family: var(--body-font);
    font-weight: 300;
    margin-bottom: 1rem;
    color: #2d3339;
  }

  :global(a) {
    text-decoration: underline;
  }

  :global(h1) {
    font-size: clamp(1.75rem, 10vw, 2.25rem);
    font-weight: 600;
    letter-spacing: -0.08rem;
    margin-bottom: 1rem;
    line-height: 1.1;
  }

  :global(h2) {
    font-size: clamp(1.5rem, 10vw, 2.125rem);
    font-weight: 600;
    margin-bottom: 1rem;
    letter-spacing: -0.06rem;
    line-height: 1.1;
  }

  :global(h3) {
    font-size: clamp(1.25rem, 10vw, 1.75rem);
    font-weight: 600;
    margin-bottom: 0.5rem;
    letter-spacing: -0.05rem;
    line-height: 1.1;
  }

  :global(ul) {
    list-style: disc;
    padding: 0.25rem 0;
    padding-left: 1.25rem;
  }

  :global(ol) {
    list-style: decimal;
    padding-left: 1.25rem;
    margin-bottom: 2rem;
  }

  :global(blockquote) {
    padding: 2rem;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

